mapper.select:
SELECT * FROM ( SELECT s.id, s.classify, s.content, s.cover_plan_url, s.create_at, s.create_by, s.grade, s.introduce, s.status, s.study_type, s.subject, s.title, s.update_at, s.update_by, s.video_time, s.video_url, s.author, (SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = s.id) AS praise, (SELECT COUNT(id) FROM study_user WHERE type = 2 AND study_id = s.id) AS collect, au.author_name, au.author_img FROM study s INNER JOIN author au ON s.author = au.id ) temp WHERE 1=1
AND study_type = 99
AND study_type = #{study_type}
AND title LIKE '%${title}%'
AND author_name LIKE '%${author}%'
AND id = #{id}
AND classify = #{classify}
AND grade = #{grade}
AND subject = #{subject}
AND praise >= ${praise[0]}
AND ${praise[1]} >= praise
AND collect >= ${collect[0]}
AND ${collect[1]} >= collect
AND status = #{status}
order by update_at DESC

SELECT sd.id, sd.title, sd.`status`, (SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = sd.id) AS praise, (SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = sd.id AND user_id = #{stu_id}) AS praise_status, (SELECT COUNT(id) FROM study_user WHERE type = 2 AND study_id = sd.id) AS collect, (SELECT COUNT(id) FROM study_user WHERE type = 2 AND study_id = sd.id AND user_id = #{stu_id}) AS collect_status, sd.create_at, sd.introduce, sd.cover_plan_url, sd.video_time AS video_time, au.author_img, au.author_name FROM study sd inner join author au ON sd.author = au.id WHERE 1 = 1 AND sd.`status` = 1
AND sd.study_type = 99
AND sd.classify = 2 AND sd.study_type = #{study_type}
AND sd.grade = #{grade}
AND sd.subject = #{subject}
order by sd.update_at ASC

SELECT IF (
        COUNT(study_id) > 0,
        1,
        0
    ) FROM study_user WHERE study_id = #{studyId} AND user_id = #{userId} AND type = #{type}
