mapper.select:
SELECT * FROM (
SELECT
s.id, s.classify, s.content, s.cover_plan_url, s.create_at, s.create_by, s.grade,
s.introduce, s.status, s.study_type, s.subject, s.title, s.update_at, s.update_by,
s.video_time, s.video_url, s.author,
(SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = s.id) AS praise,
(SELECT COUNT(id) FROM study_user WHERE type = 2 AND study_id = s.id)  AS collect,
au.author_name, au.author_img
FROM
study s INNER JOIN author au ON s.author = au.id ) temp
WHERE 1=1

    AND study_type = 99


    AND study_type = #{study_type}

        AND title LIKE '%${title}%'


        AND author_name LIKE '%${author}%'


        AND id = #{id}


        AND classify = #{classify}


        AND grade = #{grade}


        AND subject = #{subject}



            AND praise >= ${praise[0]}


            AND ${praise[1]} >= praise




            AND collect >= ${collect[0]}


            AND ${collect[1]} >= collect



        AND status = #{status}


order by update_at

SELECT sd.id, sd.title, (SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = sd.id) AS praise, (SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = sd.id AND user_id = #{stu_id}) AS praise_status, (SELECT COUNT(id) FROM study_user WHERE type = 2 AND study_id = sd.id) AS collect, (SELECT COUNT(id) FROM study_user WHERE type = 2 AND study_id = sd.id AND user_id = #{stu_id}) AS collect_status, sd.create_at, sd.introduce, sd.cover_plan_url, sd.video_time AS video_time, au.author_img, au.author_name FROM study sd inner join author au ON sd.author = au.id WHERE 1 = 1
AND sd.study_type = 99
AND sd.study_type = #{study_type}
AND sd.grade = #{grade}
AND sd.subject = #{subject}
AND sd.classify = 2
order by sd.update_at ASC

SELECT * FROM ( SELECT sd.id, sd.cover_plan_url, sd.title, sd.study_type, (SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = sd.id) AS praise FROM study sd ) temp WHERE 1 = 1
AND study_type = 99
AND study_type = #{study_type}
AND grade = #{grade}
AND subject = #{subject}
AND classify = 1
order by praise DESC

SELECT st.id, st.title, st.create_at, au.author_name, st.introduce, st.video_url, st.video_time, st.cover_plan_url, st.content, (SELECT COUNT(id) FROM study_user WHERE type = 1 AND study_id = st.id) AS praise, (SELECT COUNT(id) FROM study_user WHERE type = 2 AND study_id = st.id) AS collect,
IF ( COUNT(spraise_s.study_id) > 0, 1, 0 ) AS praise_status,
IF ( COUNT(scollect_s.study_id) > 0, 1, 0 ) AS collect_status FROM study st LEFT OUTER JOIN author au ON au.id = st.author LEFT OUTER JOIN study_user spraise_s ON spraise_s.type = 1 AND spraise_s.user_id = #{userId} LEFT OUTER JOIN study_user scollect_s ON scollect_s.type = 2 AND scollect_s.user_id = #{userId} WHERE st.id = #{studyId}

SELECT IF (
        COUNT(study_id) > 0,
        1,
        0
    ) FROM study_user WHERE study_id = #{studyId} AND user_id = #{userId} AND type = #{type}
